import { useRef} from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom';
import { useDispatch } from 'react-redux'
import { loginHandler } from '../../redux/userReducer';


export const AddExpense = () => {



    const inputTitle = useRef()
    const inputAmount = useRef()

    const dispatch = useDispatch()


    const submitHandler = (e)=>{
      e.preventDefault() 

      

      const request = async ()=>{
        const res = await axios({
          method:'POST', 
          url:'https:\\localhost:8000/login', 
          data: {email: inputTitle.current.value, password:inputAmount.current.value}
        })
        .then(res=>{
          console.log('Data in Response', res)
          console.log("token==", res.data.accessToken)
          dispatch(loginHandler(res.data.accessToken))
          
        })
        .catch(err=> console.log('error in api call ' , err))
        console.log('bs Yunhi ', res)
      }
      request()
      inputTitle.current.value=''
      inputAmount.current.value=''
    }

  return (<>
  <div className='m-auto p-5 '>
    <h5>Add New Expense</h5>
          <form  onSubmit={submitHandler}>
            <input type='text' ref={inputTitle}  placeholder='expense title' className='form-control my-3'/> 
            <input type='number' ref={inputAmount}  placeholder='expense amount' className='form-control'/> 
            <button className='btn btn-success my-3' value='submit' type='submit'>Add </button>
          </form>
        </div>  
  </>);
};
