import { useRef} from 'react'
import axios from 'axios'


export const SignUp = () => {

    const inputUserName = useRef()
    const inputPassword = useRef()


    const submitHandler = (e)=>{
      e.preventDefault() 
      
      const request = async ()=>{
        const res = await axios({
          method:'POST', 
          url:'https:\\localhost:8000/signup', 
          data: {
            username: inputUserName.current.value, 
            password:inputPassword.current.value}
        })
        .then(data=>{console.log('Data in Response', data)})
        .catch(err=> console.log('error in api call ' , err))
        console.log('bs Yunhi ', res)
      }

      request()
    }

  return (<>
  <div className='col-md-3 m-auto'>
         <h3 className='display-4 my-3'>Sign Up</h3>
          <form  onSubmit={submitHandler}>
          <input type='text' ref={inputUserName}  placeholder='full name' className='form-control my-3'/> 
            <div className='row justify-content-center align-items-center'>
              <div className='col'>
              <select type="select" className='form-select'>
              <option value="male">male</option>
              <option value="female">female</option>
              <option value="other">female</option>
            </select>
               </div>
               <div className='col'>
                <input type='text' ref={inputUserName}  placeholder='age' className='form-control my-3'/> 
               </div>
            </div>
            
            <input type='text' ref={inputUserName}  placeholder='email' className='form-control my-3'/> 
            <input type='password' ref={inputPassword}  placeholder='pasword' className='form-control'/> 
            <button className='btn btn-success my-3' value='submit' type='submit'>Sign Up</button>
          </form>
        </div>  
  </>);
};
